    agent {
        docker { image 'python:3.10' }
    }

    stages {
        stage('Build') {
            steps {
                sh 'pip install -r requirements.txt'
                sh 'python manage.py test'
            }
        }

        stage('Test') {
            steps {
                sh 'python manage.py test'
            }
        }

    }
}
